<script lang="ts">
	import { base } from '$app/paths';

	// Function to handle wheel event and transform vertical scroll to horizontal
	function handleWheel(event) {
		event.preventDefault(); // Prevent the default vertical scroll

		const container = event.currentTarget; // Reference to the scroll container

		// Adjust the scroll position horizontally using the vertical wheel delta
		container.scrollLeft += event.deltaY;
	}

	let pages = [
		{
			title: 'Page 1',
			image: `${base}/examples/1.png`
		},
		{
			title: 'Page 2',
			image: `${base}/examples/2.png`
		},
		{
			title: 'Page 3',
			image: `${base}/examples/3.png`
		}
	];

	let selectedPage: number = 1;
</script>

<div class="relative">
	<!-- Content -->

	<div
		class="absolute top-0 right-0 bottom-0 left-0 pl-[230px] flex gap-8 overflow-scroll pr-10"
		on:wheel={handleWheel}
	>
		<div class="my-4">
			<img
				draggable="false"
				class="min-w-[500px] min-w-[500px] h-auto drop-shadow-custom"
				src={pages[selectedPage].image}
				alt=""
			/>
		</div>
		<div class="bg-blue-300 min-w-[500px] my-6 rounded p-4">w1</div>
		<div class="bg-blue-400 min-w-[500px] my-6 rounded p-4">w2</div>
		<div class="bg-blue-500 min-w-[500px] my-6 rounded p-4">w3</div>
	</div>

	<!-- Page selector -->
	<div
		class="w-[200px] bg-primary/60 rounded-lg backdrop-blur-xl grid grid-rows-[1fr_auto] overflow-hidden absolute top-4 left-4 bottom-4 p-4"
	>
		<!-- Pages -->
		<div class="overflow-auto">
			<!-- for each page in the document -->
			{#each pages as page, index}
				<button class="mb-4" on:click={() => (selectedPage = index)}>
					<img
						draggable="false"
						class="drop-shadow-custom rounded-lg border-4 border-transparent select-none hover:border-white transition"
						class:!border-secondary={selectedPage == index}
						src={page.image}
						alt={page.title}
					/>
					{index}
				</button>
			{/each}
